"""
Complete Nagpur Coverage Download Script
Downloads BOTH row 045 and 046 scenes from same dates for complete city coverage
"""

import os
from urllib.request import urlopen
import planetary_computer

def download_band(url, output_path):
    """Download a band from URL with progress"""
    try:
        os.makedirs(os.path.dirname(output_path), exist_ok=True)
        signed_url = planetary_computer.sign(url)
        
        print(f"Downloading: {os.path.basename(output_path)}")
        with urlopen(signed_url) as response:
            file_size = int(response.headers.get('Content-Length', 0))
            with open(output_path, 'wb') as out_file:
                downloaded = 0
                while True:
                    chunk = response.read(8192)
                    if not chunk:
                        break
                    out_file.write(chunk)
                    downloaded += len(chunk)
                    if file_size > 0:
                        progress = (downloaded / file_size) * 100
                        print(f"  Progress: {progress:.1f}%", end="\r")
        
        print(f"  SUCCESS: Completed: {os.path.basename(output_path)}")
        return True
        
    except Exception as e:
        print(f"  ERROR downloading {os.path.basename(output_path)}: {e}")
        return False

def download_scene_pair(row_045_info, row_046_info, period_name, date_str):
    """Download both scenes for complete Nagpur coverage"""
    
    print(f"\n{'='*80}")
    print(f"DOWNLOADING COMPLETE COVERAGE FOR: {period_name}")
    print(f"Date: {date_str}")
    print(f"{'='*80}")
    
    # Create organized directory structure
    base_dir = f"data/{period_name.lower().replace(' ', '_').replace('(', '').replace(')', '')}"
    row_045_dir = f"{base_dir}/row_045_{row_045_info['scene_id']}"
    row_046_dir = f"{base_dir}/row_046_{row_046_info['scene_id']}"
    
    print(f"\nRow 045 Scene: {row_045_info['scene_id']} ({row_045_info['cloud_cover']:.1f}% clouds)")
    print(f"Row 046 Scene: {row_046_info['scene_id']} ({row_046_info['cloud_cover']:.1f}% clouds)")
    
    success_count = 0
    total_bands = 0
    
    for row_info, row_dir, row_name in [
        (row_045_info, row_045_dir, "Row 045"),
        (row_046_info, row_046_dir, "Row 046")
    ]:
        print(f"\n--- Downloading {row_name} ---")
        
        for band_key, band_info in row_info['bands'].items():
            if isinstance(band_info, dict) and band_info.get('available') and band_info.get('url'):
                band_name = band_info.get('band_number', band_key)
                filename = f"{row_info['scene_id']}_{band_name}.TIF"
                output_path = f"{row_dir}/{filename}"
                
                total_bands += 1
                if download_band(band_info['url'], output_path):
                    success_count += 1
    
    print(f"\n--- Download Summary for {period_name} ---")
    print(f"Successfully downloaded: {success_count}/{total_bands} bands")
    print(f"Row 045 data: {row_045_dir}")
    print(f"Row 046 data: {row_046_dir}")
    
    if success_count == total_bands:
        print("SUCCESS: COMPLETE NAGPUR COVERAGE SUCCESSFULLY DOWNLOADED!")
    else:
        print("WARNING: Some downloads failed - check network connection")
    
    return success_count == total_bands

